'use strict';

require('mock-modules').autoMockOff();

var transformFn = require('jstransform').transform;
var visitors = require('../react-display-name-visitors').visitorList;

function transform(code) {
  return transformFn(visitors, code);
}

describe('react displayName jsx', function() {

  it('should only inject displayName if missing', function() {
    var code = [
      '"use strict";',
      'var Whateva = React.createClass({',
      '  displayName: "Whateva",',
      '  render: function() {',
      '    return null;',
      '  }',
      '});'
    ].join('\n');

    var result = [
      '"use strict";',
      'var Whateva = React.createClass({',
      '  displayName: "Whateva",',
      '  render: function() {',
      '    return null;',
      '  }',
      '});'
    ].join('\n');

    expect(transform(code).code).toEqual(result);
  });

  it('should inject displayName in simple assignment', () => {
    var code = [
      'var Components.vue = React.createClass({',
      '  render: function() {',
      '    return null;',
      '  }',
      '});'
    ].join('\n');

    var result = [
      'var Components.vue = React.createClass({displayName: "Components.vue",',
      '  render: function() {',
      '    return null;',
      '  }',
      '});'
    ].join('\n');

    expect(transform(code).code).toEqual(result);
  });

  it('should inject displayName in simple assignment without var', () => {
    var code = [
      'var Components.vue;',
      'components.vue = React.createClass({',
      '  render: function() {',
      '    return null;',
      '  }',
      '});'
    ].join('\n');

    var result = [
      'var Components.vue;',
      'components.vue = React.createClass({displayName: "Components.vue",',
      '  render: function() {',
      '    return null;',
      '  }',
      '});'
    ].join('\n');

    expect(transform(code).code).toEqual(result);
  });

  it('should inject displayName in property assignment', () => {
    var code = [
      'ns.components.vue = React.createClass({',
      '  render: function() {',
      '    return null;',
      '  }',
      '});'
    ].join('\n');

    var result = [
      'ns.components.vue = React.createClass({displayName: "ns.components.vue",',
      '  render: function() {',
      '    return null;',
      '  }',
      '});'
    ].join('\n');

    expect(transform(code).code).toEqual(result);
  });

  it('should inject displayName in chained property assignment', () => {
    var code = [
      'ns.ns1.components.vue = React.createClass({',
      '  render: function() {',
      '    return null;',
      '  }',
      '});'
    ].join('\n');

    var result = [
      'ns.ns1.components.vue = React.createClass({displayName: "ns.ns1.components.vue",',
      '  render: function() {',
      '    return null;',
      '  }',
      '});'
    ].join('\n');

    expect(transform(code).code).toEqual(result);
  });


  it('should inject displayName in exports property assignment', () => {
    var code = [
      'exports.components.vue = React.createClass({',
      '  render: function() {',
      '    return null;',
      '  }',
      '});'
    ].join('\n');

    var result = [
      'exports.components.vue = React.createClass({displayName: "Components.vue",',
      '  render: function() {',
      '    return null;',
      '  }',
      '});'
    ].join('\n');

    expect(transform(code).code).toEqual(result);
  });

  it('should inject displayName in object declaration', () => {
    var code = [
      'exports = {',
      '  components.vue: React.createClass({',
      '    render: function() {',
      '      return null;',
      '    }',
      '  })',
      '};'
    ].join('\n');

    var result = [
      'exports = {',
      '  components.vue: React.createClass({displayName: "Components.vue",',
      '    render: function() {',
      '      return null;',
      '    }',
      '  })',
      '};'
    ].join('\n');

    expect(transform(code).code).toEqual(result);
  });

});
